import tkinter as tkfrom tkinter import messageboximport timeimport sysimport osimport subprocessdef prank_window():    root = tk.Tk()    root.title("SYSTEM ALERT: VIRUS DETECTED! ??")    root.attributes('-fullscreen', True)  # Fullscreen for max prank effect    root.configure(bg='black')    # Fun, silly NSFW joke - change this to whatever horny pun you like    joke_text = """    OH NO! Your computer has been infected with the FUN PORN VIRUS! ????        For 60 seconds, enjoy this wisdom:    Why did the penis go to school?     To get a little head! ????        (It's harmless, just a prank! Closing in 60 seconds...)    """        label = tk.Label(root, text=joke_text, font=('Arial', 24, 'bold'), fg='pink', bg='black')    label.pack(expand=True)    # Countdown    def countdown(seconds_left):        if seconds_left > 0:            time_label = tk.Label(root, text=f"Time left: {seconds_left} seconds", font=('Arial', 18), fg='red', bg='black')            time_label.pack()            root.after(1000, lambda: countdown(seconds_left - 1))        else:            root.quit()    countdown(60)    root.mainloop()    # Self-delete after closing    def delete_self():        file_path = sys.executable if getattr(sys, 'frozen', False) else os.path.abspath(__file__)        try:            subprocess.Popen(f'ping 127.0.0.1 -n 2 > nul & del /F /Q "{file_path}"', shell=True)        except:            pass    delete_self()if __name__ == "__main__":    prank_window()