user_answers = []

current_question = 0
score = 0

def next_question():
    global current_question
    selected = var.get()
    user_answers.append(selected)
    var.set(None)
    current_question += 1

    if current_question < len(questions):
        update_question()
    else:
        show_result()

def update_question():
    q_label.config(text=f"س: {questions[current_question]}")
    r1.config(text=options_a[current_question], value=options_a[current_question])
    r2.config(text=options_b[current_question], value=options_b[current_question])
    r3.config(text=options_c[current_question], value=options_c[current_question])

def show_result():
    global score
    for i in range(len(questions)):
        if user_answers[i] == correct_answers[i]:
            score += 1
    percent = (score / len(questions)) * 100
    messagebox.showinfo("النتيجة", f"درجتك هي: {percent:.2f}٪")
    root.destroy()

